CREATE DATABASE hoteles_caba;
USE hoteles;

CREATE TABLE hoteles (
  id INT AUTO_INCREMENT PRIMARY KEY,
  nombre VARCHAR(255),
  sector VARCHAR(100),
  precio DECIMAL(10,2),
  estrellas INT,
  descripcion TEXT,
  imagen varchar(400)
);

